        import sqlite3
        #Connect to the database
        c = sqlite3.connect('NRM.db')
        cursor = c.cursor()
        #create stores tables
        command1 = """CREATE TABLE IF NOT EXISTS
        users(usr_id INTEGER PRIMARY KEY, username TEXT, password TEXT)"""
        cursor.execute(command1)
        cursor.execute("INSERT INTO users VAlUES(1, 'admin','admin')")
        cursor.execute("INSERT INTO users VAlUES(2, 'user','user')")
        cursor.execute("SELECT * FROM users")
        users=cursor.fetchall()
        usr_name = self.lineEdit.text()
        psswrd = self.password.text()
        print(users)
        if usr_name != '' and psswrd != '':
            for user in users:
                if user[1] == usr_name and user[2] == psswrd:
                    self.window = QtWidgets.QMainWindow()
                    self.ui = Ui_Main_Window()
                    self.ui.setupUi(self.window)
                    self.window.show()
                    LoginWindow.hide()
                else:
                    print('username or password is wrong')
        else:
            print('something is wrong man')
